<script setup>
import { reactive, ref } from 'vue';
import { useRoute, useRouter } from 'vue-router';
import Cookies from 'js-cookie';

const route = useRoute()
const router = useRouter()

const loginForm = reactive({
  username: null,
  password: null
})
const isShowError = ref(false)
const disabled = ref(false)

const handleLogin =() => {
  if (loginForm.username?.trim().length > 0 && loginForm.password === process.env.VUE_APP_DEFAULT_PASSWORD) {
    Cookies.set('isAuthenticated', loginForm.username, { expires: 1 })
    if (route.redirectedFrom?.path) {
      router.push(route.redirectedFrom.path)
    } else {
      router.push('/')
    }
  } else {
    isShowError.value = true
    disabled.value = true
  }
  setTimeout(() => {
    disabled.value = false
  }, 1500)
}
</script>
